INSERT INTO `mdl_scale` VALUES (1,0,0,'Formas de saber: conectada e destacada','Sobretudo saber destacado,Destacado e conectado,Sobretudo saber conectado','A escala  baseada na teoria de conhecimento separado e conectado. Esta teoria descreve duas maneiras diferentes de avaliar e aprender sobre as coisas que vemos e ouvimos.<ul><li><strong>Conhecedores separados</strong> permanecem o mais objetivo possvel sem incluir semtimentos e emoes. Em uma discusso com outras pessoas, eles gostam de defender suas prprias idias, usando lgica para encontrar furos nas idias de seus oponentes.</li><li><strong>Conhecedores conectados</strong> so mais sensveis com as outras pessoas. Eles so habilidosos em empatia e preferem ouvir e questionar at que sintam que podem conectar e \"entender as coisas do seu ponto de vista\". Eles aprendem pela tentativa de compartilhar as experincias que levam ao conhecimento que eles encontram nas outras pessoas.</li></ul>',0,1450122057);
