INSERT INTO `mdl_scale` VALUES (1,0,0,'Formas de saber: conectada e destacada','Sobretudo saber destacado,Destacado e conectado,Sobretudo saber conectado','A escala é baseada na teoria de conhecimento separado e conectado. Esta teoria descreve duas maneiras diferentes de avaliar e aprender sobre as coisas que vemos e ouvimos.<ul><li><strong>Conhecedores separados</strong> permanecem o mais objetivo possível sem incluir semtimentos e emoções. Em uma discussão com outras pessoas, eles gostam de defender suas próprias idéias, usando lógica para encontrar furos nas idéias de seus oponentes.</li><li><strong>Conhecedores conectados</strong> são mais sensíveis com as outras pessoas. Eles são habilidosos em empatia e preferem ouvir e questionar até que sintam que podem conectar e \"entender as coisas do seu ponto de vista\". Eles aprendem pela tentativa de compartilhar as experiências que levam ao conhecimento que eles encontram nas outras pessoas.</li></ul>',0,1450122057);
